import React, { useEffect, useState } from 'react'
import HeroService from '../../../component/service/HeroService';
import ConsultationForm from '../../../component/Consultation';
import ToToServices from './sections/ToToService';
import TrademarkOpposition from './sections/TrademarkOpposition';
import { useParams } from 'react-router-dom';
import { Hero } from '../../../types/service';
import { getHeroByServiceId } from '../../../service/service';
import { UniversalProcess } from '../../../component/service/UniversalProcess';
import { ProcessTimeline } from '../../../component/service/ProcessTimeLine';
export default function Index() {
  const { id } = useParams<{ id: string }>();
    const [hero, setHero] = useState<Hero>({
        title: 'Dịch vụ sở hữu trí tuệ',
        subtitle: 'ToTo Law',
        description: ''
      });
      useEffect(() => {
        const fetchHero = async () => {
          try {
            if (id) {
              const response = await getHeroByServiceId(id);
              setHero(response.data);
            } else {
              console.error('Service ID is undefined');
            }
          } catch (error) {
            console.error('Failed to fetch hero:', error);
          }
        };
        fetchHero();
      }, [id]);
      const process = [
        {
          id: 1,
          step :"Bước 1",
          title: 'Tiếp nhận hồ sơ',
          description: 'Kiểm tra và tiếp nhận đơn phản đối từ khách hàng',
        },
        {
          id: 2,
          step :"Bước 2",
          title: 'Xử lý đơn',
          description: 'Thẩm định và xử lý đơn phản đối theo quy định',
        },
        {
          id: 3,
          step :"Bước 3",
          title: 'Kết quả',
          description: 'Thông báo kết quả xử lý đơn phản đối',
        }
      ]
      const defaultSteps = [
        {
          icon: 'clock',
          title: '',
          description: 'Thời gian để Cục SHTT xem xét yêu cầu phản đối đơn đăng ký nhãn hiệu là từ 06-09 tháng kể từ ngày nộp hồ sơ.',
          duration : " 6 -9 tháng",
          color: "blue" as "blue"
        },
    ];
  return (
    <>
      <HeroService
        title={hero.title}
        subtitle={hero.subtitle}
        description={hero.description}
        showCTA={true}
        ctaText="Tư vấn miễn phí"
        onCTAClick={() => {
                const contactForm = document.getElementById('contact-form');
                if (contactForm) {
                  contactForm.scrollIntoView({ behavior: 'smooth' });
                }
        }}

      />
            <ToToServices />
            <UniversalProcess
              title="QUY TRÌNH PHẢN ĐỐI ĐƠN"
              steps={process} />
              <ProcessTimeline
                title="THỜI GIAN THỰC HIỆN"
                subtitle=""
                steps={defaultSteps}
                layout="horizontal"
                showConnectors={true}
            />
            <TrademarkOpposition />
        <ConsultationForm />
    </>
  )
}
