import React, { useState } from 'react';
import { Search, Menu, X, ChevronDown } from 'lucide-react';

interface MenuItemProps {
  id: string;
  title: string;
  href: string;
  children?: MenuItemProps[];
}

const TagaHeader: React.FC = () => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeSubmenu, setActiveSubmenu] = useState<string | null>(null);

  const menuItems: MenuItemProps[] = [
    { id: 'home', title: 'Trang chủ', href: '/' },
    { id: 'about', title: 'Về chúng tôi', href: '/ve-chung-toi' },
    {
      id: 'ip-services',
      title: 'Dịch vụ sở hữu trí tuệ',
      href: '/dich-vu-so-huu-tri-tue',
      children: [
        { id: 'trademark', title: 'Dịch vụ đăng ký nhãn hiệu thương hiệu độc quyền', href: '/dang-ky-bao-ho-nhan-hieu' },
        { id: 'copyright', title: 'Dịch vụ đăng ký bảo hộ bản quyền', href: '/dang-ky-bao-ho-ban-quyen' },
        { id: 'design', title: 'Dịch vụ Đăng ký bảo hộ kiểu dáng công nghiệp', href: '/bao-ho-kieu-dang-cong-nghiep' },
        { id: 'patent', title: 'Đăng ký bảo hộ Sáng chế, giải pháp hữu ích', href: '/bao-ho-sang-che-giai-phap-huu-ich' },
        { id: 'infringement', title: 'Dịch vụ xử lý xâm phạm sở hữu trí tuệ', href: '/xu-ly-xam-pham' }
      ]
    },
    {
      id: 'other-services',
      title: 'Dịch vụ khác',
      href: '/dich-vu-khac',
      children: [
        { id: 'investment', title: 'Xin cấp Giấy chứng nhận đầu tư tại Việt Nam', href: '/xin-cap-giay-chung-nhan-dau-tu-tai-viet-nam' },
        { id: 'barcode', title: 'Dịch vụ đăng ký mã số mã vạch', href: '/dang-ky-ma-so-ma-vach' },
        { id: 'tech-license', title: 'Giấy phép Doanh nghiệp Khoa học Công nghệ', href: '/giay-phep-doanh-nghiep-khoa-hoc-cong-nghe' },
        { id: 'social-media', title: 'Đăng ký giấy phép mạng xã hội', href: '/dang-ky-giay-phep-mang-xa-hoi' },
        { id: 'contract', title: 'Dịch vụ Tư vấn soạn thảo Hợp đồng', href: '/tu-van-soan-thao-hop-dong' }
      ]
    },
    {
      id: 'news',
      title: 'Bản tin',
      href: '#',
      children: [
        { id: 'legal-news', title: 'Bản tin pháp luật', href: '/chuyen-muc/ban-tin-phap-luat' },
        { id: 'useful-info', title: 'Thông tin hữu ích', href: '/chuyen-muc/thong-tin-huu-ich' },
        { id: 'recruitment', title: 'Tuyển dụng', href: '/tuyen-dung' }
      ]
    },
    { id: 'qa', title: 'Hỏi đáp', href: '/chuyen-muc/hoi-dap' },
    { id: 'contact', title: 'Liên hệ', href: '/lien-he' }
  ];

  const toggleSubmenu = (itemId: string) => {
    setActiveSubmenu(activeSubmenu === itemId ? null : itemId);
  };

  const renderMenuItem = (item: MenuItemProps, isMobile = false) => (
    <li key={item.id} className={`relative ${item.children ? 'group' : ''}`}>
      <div className="flex items-center">
        <a
          href={item.href}
          className={`block px-4 py-2 text-gray-700 hover:text-blue-600 transition-colors duration-200 ${
            isMobile ? 'text-base' : 'text-sm'
          }`}
        >
          {item.title}
        </a>
        {item.children && (
          <button
            onClick={() => toggleSubmenu(item.id)}
            className={`ml-1 p-1 ${isMobile ? 'block' : 'hidden group-hover:block'}`}
          >
            <ChevronDown 
              size={16} 
              className={`transform transition-transform ${
                activeSubmenu === item.id ? 'rotate-180' : ''
              }`}
            />
          </button>
        )}
      </div>
      
      {item.children && (
        <ul
          className={`${
            isMobile 
              ? `pl-4 ${activeSubmenu === item.id ? 'block' : 'hidden'}`
              : 'absolute left-0 top-full w-80 bg-white shadow-lg border border-gray-200 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200 z-50'
          }`}
        >
          {item.children.map(child => (
            <li key={child.id}>
              <a
                href={child.href}
                className={`block px-4 py-2 text-gray-600 hover:text-blue-600 hover:bg-gray-50 transition-colors duration-200 ${
                  isMobile ? 'text-sm' : 'text-sm border-b border-gray-100 last:border-b-0'
                }`}
              >
                {child.title}
              </a>
            </li>
          ))}
        </ul>
      )}
    </li>
  );

  return (
    <header className="bg-white shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <div className="flex-shrink-0">
            <a href="/" className="flex items-center">
              <img
                src="https://luattaga.vn/wp-content/uploads/2023/04/TAGA-5-1.png"
                alt="TAGA Logo"
                className="h-10 w-auto"
              />
            </a>
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden lg:block">
            <ul className="flex space-x-1">
              {menuItems.map(item => renderMenuItem(item))}
            </ul>
          </nav>

          {/* Right Side Icons */}
          <div className="flex items-center space-x-4">
            {/* Search Button */}
            <button
              onClick={() => setIsSearchOpen(!isSearchOpen)}
              className="p-2 text-gray-600 hover:text-blue-600 transition-colors duration-200"
              aria-label="Search"
            >
              <Search size={20} />
            </button>

            {/* Mobile Menu Button */}
            <button
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              className="lg:hidden p-2 text-gray-600 hover:text-blue-600 transition-colors duration-200"
              aria-label="Mobile menu"
            >
              {isMobileMenuOpen ? <X size={20} /> : <Menu size={20} />}
            </button>
          </div>
        </div>

        {/* Search Bar */}
        {isSearchOpen && (
          <div className="border-t border-gray-200 py-4">
            <div className="relative">
              <input
                type="text"
                placeholder="Nhập từ khóa tìm kiếm"
                className="w-full px-4 py-2 pr-10 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                autoFocus
              />
              <button
                onClick={() => setIsSearchOpen(false)}
                className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
              >
                <X size={20} />
              </button>
            </div>
          </div>
        )}

        {/* Mobile Navigation */}
        {isMobileMenuOpen && (
          <div className="lg:hidden border-t border-gray-200 py-4">
            <nav>
              <ul className="space-y-2">
                {menuItems.map(item => renderMenuItem(item, true))}
              </ul>
            </nav>
          </div>
        )}
      </div>
    </header>
  );
};

export default TagaHeader;
