import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { Briefcase, MapPin, Calendar} from 'lucide-react';
import { jobs } from './jobData';

interface JobPostingProps {
  jobData?: {
    id: number;
    title: string;
    company?: string;
    jobType: string;
    location: string;
    postedDate: string;
    description: string[];
    requirements: string[];
    benefits: string[];
    category: string;
  };
}

const Job: React.FC<JobPostingProps> = ({ jobData }) => {
  const navigate = useNavigate();
  const { id } = useParams();
  console.log('Job ID:', id);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);

  const currentJob = jobData || jobs.find(job => job.id.toString() === id);

  if (!currentJob) {
    return (
      <div className="max-w-4xl mx-auto p-6 bg-white">
        <h1 className="text-2xl font-bold text-red-500">Không tìm thấy công việc</h1>
        <button 
          onClick={() => navigate('/recruitment')}
          className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700"
        >
          Quay lại danh sách
        </button>
      </div>
    );
  }

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    if (!selectedFile) {
      alert('Vui lòng đính kèm CV của bạn');
      return;
    }
    alert(`Đã nộp đơn ứng tuyển thành công cho vị trí ${currentJob.title}!`);
    navigate('/jobs');
  };

  const ListWithBullets = ({ items, bulletColor = 'bg-blue-500' }: { items: string[], bulletColor?: string }) => (
    <ul className="space-y-3 text-gray-700">
      {items.map((item, index) => (
        <li key={index} className="flex items-start gap-2">
          <span className={`w-2 h-2 ${bulletColor} rounded-full mt-2 flex-shrink-0`}></span>
          <span>{item}</span>
        </li>
      ))}
    </ul>
  );

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white">
      {/* Header */}
      <header className="mb-8">
        <div className="border-b pb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">
            {currentJob.title}
          </h1>
          
          <div className="flex flex-wrap gap-6 text-gray-600">
            <div className="flex items-center gap-2">
              <Briefcase className="w-5 h-5" />
              <span>{currentJob.jobType}</span>
            </div>
            <div className="flex items-center gap-2">
              <MapPin className="w-5 h-5" />
              <span>{currentJob.location}</span>
            </div>
            <div className="flex items-center gap-2">
              <Calendar className="w-5 h-5" />
              <span>{currentJob.postedDate}</span>
            </div>
          </div>
        </div>
      </header>

      {/* Các phần mô tả công việc, yêu cầu, phúc lợi... */}
      {/* Giữ nguyên như bản gốc của bạn, nhưng sử dụng currentJob thay vì jobData */}

      {/* Nút quay lại */}
      <div className="mt-8">
        <button 
          onClick={() => navigate('/jobs')}
          className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold py-2 px-6 rounded-lg transition-colors duration-200"
        >
          Quay lại danh sách
        </button>
      </div>
    </div>
  );
};

export default Job;
