import React, { useState } from "react";
import serviceServiceInstance  from "../../../service/service";
import { Service } from "../../../model/serviceModel";

interface DropdownProps {
  title: string;
  api: string; // Thêm prop để truyền API endpoint
}

const Dropdown: React.FC<DropdownProps> = ({ title, api }) => {
  const [menu, setMenu] = useState<Service[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  const fetchListService = async () => {
    try {
      setIsLoading(true);
      const response = await serviceServiceInstance .getService(api);
      setMenu(response);
    } catch (error) {
      console.error("Lỗi khi lấy danh sách dịch vụ:", error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <li
      className="relative group"
      onMouseEnter={fetchListService} // Gọi API khi hover
    >
      <button
        className="flex items-center justify-between w-full py-2 px-3 text-gray-900 rounded-sm hover:bg-gray-100 
        md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 md:w-auto dark:text-white md:dark:hover:text-blue-500 
        dark:focus:text-white dark:border-gray-700 dark:hover:bg-gray-700 md:dark:hover:bg-transparent"
      >
        <a href="">{title}</a>
        <svg
          className="w-2.5 h-2.5 ms-2.5"
          aria-hidden="true"
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 10 6"
        >
          <path
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="m1 1 4 4 4-4"
          />
        </svg>
      </button>
      {/* Dropdown hiển thị khi hover vào nút */}
      <div className="absolute top-full left-0 z-10 hidden group-hover:block font-normal bg-white divide-y divide-gray-100 rounded-lg shadow-sm w-44 dark:bg-gray-700 dark:divide-gray-600">
        <ul className="py-2 text-sm text-gray-700 dark:text-gray-400">
          {isLoading ? (
            <li className="px-4 py-2 text-center">Đang tải...</li>
          ) : (
            menu.map((item) => (
              <li key={item.id}>
                <a
                  href="#"
                  className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white"
                >
                  {item.name}
                </a>
              </li>
            ))
          )}
        </ul>
      </div>
    </li>
  );
};

export default Dropdown;
