import React from 'react';
import { Barcode } from 'lucide-react'; // Import an icon from lucide-react

const BarcodeSection: React.FC = () => {
    return (
        <section className="p-6 bg-gray-100 rounded-lg shadow-md">
            <div className="text-center mb-4">
                <h2 className="text-2xl font-bold text-gray-800">
                    <Barcode className="inline-block mr-2" /> {/* Icon */}
                    MÃ SỐ MÃ VẠCH - CHỨNG MINH THƯ CỦA SẢN PHẨM
                </h2>
            </div>
            <div className="flex flex-col md:flex-row items-start">
                <div className="flex-shrink-0 mb-4 md:mb-0 md:mr-4">
                    <img 
                        width={293} 
                        height={317} 
                        src="../../i0.wp.com/luattaga.vn/wp-content/uploads/2023/04/10_Jan_2019_163247_GMTbai-viet-ma-so-ma-vach-haoc1eb.jpg?fit=293%2C317&ssl=1" 
                        className="max-w-full h-auto rounded-lg shadow" 
                        alt="Mã số mã vạch" 
                        loading="lazy" 
                    />
                </div>
                <div className="flex-1 text-justify">
                    <p className="text-gray-700">
                        Mã số mã vạch là một thẻ chứng minh xuất xứ và lưu thông của sản phẩm trên các thị trường quốc tế. Mỗi sản phẩm được gắn một mã số duy nhất, tương tự như mã số điện thoại trong viễn thông. Mã vạch là dãy số dưới dạng vạch đặc biệt để máy đọc được. Hiện nay, có hai loại chính là mã số 1 chiều và mã số hai chiều. Các hệ thống mã vạch phổ biến bao gồm EAN-13, CODE 39, và QR CODE. Luật Taga cung cấp dịch vụ đăng ký mã EAN-13 cho khách hàng.
                    </p>
                </div>
            </div>
        </section>
    );
};

export default BarcodeSection;
