import React from 'react';

interface BarcodeInfoSectionProps {
  // Có thể thêm props nếu cần
}

const BarcodeInfoSection: React.FC<BarcodeInfoSectionProps> = () => {
  return (
    <div className="max-w-6xl mx-auto px-4">
      {/* Phần tiêu đề */}
      <section className="py-8">
        <h2 className="text-2xl md:text-3xl font-bold text-center">
          MÃ SỐ MÃ VẠCH - CHỨNG MINH THƯ CỦA SẢN PHẨM
        </h2>
      </section>

      {/* Phần nội dung */}
      <section className="flex flex-col md:flex-row gap-8 pb-12">
        {/* Hình ảnh */}
        <div className="md:w-1/5">
          <img
            src="https://i0.wp.com/luattaga.vn/wp-content/uploads/2023/04/10_Jan_2019_163247_GMTbai-viet-ma-so-ma-vach-hao.jpg?fit=293%2C317&ssl=1"
            alt="Mã số mã vạch"
            className="w-full h-auto object-cover rounded-lg"
            loading="lazy"
          />
        </div>

        {/* Nội dung văn bản */}
        <div className="md:w-2/3">
          <p className="text-justify text-gray-700 leading-relaxed">
            Mã số mã vạch là một thẻ chứng minh xuất xứ và lưu thông của sản phẩm trên các thị trường quốc tế. Mỗi sản phẩm được gắn một mã số duy nhất, tương tự như mã số điện thoại trong viễn thông. Mã vạch là dãy số dưới dạng vạch đặc biệt để máy đọc được. Hiện nay, có hai loại chính là mã số 1 chiều và mã số hai chiều. Các hệ thống mã vạch phổ biến bao gồm EAN-13, CODE 39, và QR CODE. Luật Taga cung cấp dịch vụ đăng ký mã EAN-13 cho khách hàng.
          </p>
        </div>
      </section>
    </div>
  );
};

export default BarcodeInfoSection;
