import React from 'react';
import { 
  ChevronLeft, 
  ChevronRight, 
  Grid3X3, 
  User, 
  Clock, 
  Facebook, 
  Twitter, 
  Linkedin, 
  Heart,
  MessageCircle
} from 'lucide-react';

interface BlogPostProps {
  post?: {
    id: number;
    title: string;
    content: string;
    author: {
      name: string;
      avatar: string;
      bio?: string;
    };
    publishedAt: string;
    likes: number;
    category: string;
  };
}

const BlogPost: React.FC<BlogPostProps> = ({ post }) => {
  const defaultPost = {
    id: 999,
    title: "Những điểm mới của Nghị định 65/2023/NĐ-CP",
    content: `
      <p>Vào ngày 23/8/2023, Chính phủ đã ban hành Nghị định số 65/2023/NĐ-CP, nhằm đưa ra các hướng dẫn cụ thể và biện pháp thực thi của Luật Sở hữu Trí tuệ liên quan đến sở hữu công nghiệp, bảo vệ quyền sở hữu công nghiệp, quyền đối với giống cây trồng và quản lý nhà nước về sở hữu trí tuệ. Nghị định này đồng thời thay thế Nghị định số 103/2006/NĐ-CP và một phần của Nghị định số 105/2006/NĐ-CP (đã được sửa đổi, bổ sung).</p>
      
      <h5><strong>Cập nhật các Mẫu đơn</strong></h5>
      
      <p>Các mẫu tờ khai cho việc đăng ký nhãn hiệu, sáng chế, kiểu dáng công nghiệp và thiết kế bố trí đã được điều chỉnh để tuân thủ mẫu mới quy định và hướng dẫn tại Phụ lục I, II và IV của Nghị định.</p>
      
      <p>Ngoài ra, để đáp ứng nhu cầu, lần đầu tiên, đã có sự xuất hiện của mẫu "Đơn đề nghị xác nhận thủ tục đăng ký lưu hành dược phẩm lần đầu bị chậm" và "Tờ khai yêu cầu đền bù do sự chậm trễ trong việc cấp phép lưu hành lần đầu đối với dược phẩm sản xuất theo bằng độc quyền sáng chế." Điều này cho phép các chủ bằng sáng chế yêu cầu cơ quan có thẩm quyền xem xét việc đền bù khi phát hiện sự chậm trễ trong việc cấp phép lưu hành dược phẩm, theo quy định tại Điều 131a của Luật Sở hữu Trí tuệ năm 2022.</p>
    `,
    author: {
      name: "Luật Taga",
      avatar: "https://secure.gravatar.com/avatar/f6e3609c50380348900fd1118daa0c99?s=64&d=mm&r=g",
      bio: ""
    },
    publishedAt: "2023-09-26T17:23:29+07:00",
    likes: 2,
    category: "ban-tin-phap-luat"
  };

  const currentPost = post || defaultPost;

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('vi-VN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleShare = (platform: string) => {
    const url = encodeURIComponent(window.location.href);
    const title = encodeURIComponent(currentPost.title);
    
    const shareUrls = {
      facebook: `https://www.facebook.com/sharer/sharer.php?u=${url}`,
      twitter: `https://twitter.com/intent/tweet?text=${title}&url=${url}`,
      linkedin: `https://www.linkedin.com/shareArticle?mini=true&url=${url}&title=${title}`
    };

    if (shareUrls[platform as keyof typeof shareUrls]) {
      window.open(shareUrls[platform as keyof typeof shareUrls], '_blank', 'width=600,height=400');
    }
  };

  return (
    <main className="max-w-4xl mx-auto px-4 py-8">
      <article className="bg-white shadow-lg rounded-lg overflow-hidden">
        {/* Header Navigation */}
        <header className="bg-gray-50 border-b border-gray-200 p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button className="p-2 hover:bg-gray-200 rounded-full transition-colors">
                <ChevronLeft className="w-5 h-5 text-gray-600" />
              </button>
              <button className="p-2 hover:bg-gray-200 rounded-full transition-colors">
                <ChevronRight className="w-5 h-5 text-gray-600" />
              </button>
              <button className="p-2 hover:bg-gray-200 rounded-full transition-colors">
                <Grid3X3 className="w-5 h-5 text-gray-600" />
              </button>
            </div>
            
            <div className="flex items-center space-x-6 text-sm text-gray-600">
              <div className="flex items-center space-x-2">
                <span className="text-gray-500">Đăng bởi</span>
                <User className="w-4 h-4" />
                <a href="#" className="text-blue-600 hover:text-blue-800 font-medium">
                  {currentPost.author.name}
                </a>
              </div>
              
              <div className="flex items-center space-x-2">
                <span className="text-gray-500">lúc</span>
                <Clock className="w-4 h-4" />
                <time className="text-gray-700">
                  {formatDate(currentPost.publishedAt)}
                </time>
              </div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <div className="p-8">
          <div className="prose prose-lg max-w-none">
            <div dangerouslySetInnerHTML={{ __html: currentPost.content }} />
          </div>

          {/* Related Posts Section */}
          <div className="mt-12 pt-8 border-t border-gray-200">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">
              <em>Bài viết liên quan</em>
            </h3>
            {/* Related posts would go here */}
          </div>
        </div>

        {/* Share Section */}
        <section className="bg-gray-50 px-8 py-6 border-t border-gray-200">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <span className="text-gray-700 font-medium">Chia sẻ</span>
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => handleShare('facebook')}
                  className="p-2 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors"
                >
                  <Facebook className="w-4 h-4" />
                </button>
                <button
                  onClick={() => handleShare('twitter')}
                  className="p-2 bg-sky-500 text-white rounded-full hover:bg-sky-600 transition-colors"
                >
                  <Twitter className="w-4 h-4" />
                </button>
                <button
                  onClick={() => handleShare('linkedin')}
                  className="p-2 bg-blue-700 text-white rounded-full hover:bg-blue-800 transition-colors"
                >
                  <Linkedin className="w-4 h-4" />
                </button>
              </div>
            </div>
            
            <button className="flex items-center space-x-2 px-4 py-2 bg-red-50 text-red-600 rounded-full hover:bg-red-100 transition-colors">
              <Heart className="w-4 h-4" />
              <span>{currentPost.likes}</span>
            </button>
          </div>
        </section>

        {/* Author Section */}
        <section className="px-8 py-6 bg-white border-t border-gray-200">
          <div className="flex items-start space-x-4">
            <img
              src={currentPost.author.avatar}
              alt={currentPost.author.name}
              className="w-16 h-16 rounded-full object-cover"
            />
            <div>
              <h5 className="text-lg font-semibold text-gray-800 mb-1">
                <a href="#" className="hover:text-blue-600 transition-colors">
                  {currentPost.author.name}
                </a>
              </h5>
              {currentPost.author.bio && (
                <p className="text-gray-600 text-sm">{currentPost.author.bio}</p>
              )}
            </div>
          </div>
        </section>

        {/* Comments Section */}
        <section className="px-8 py-6 bg-gray-50 border-t border-gray-200">
          <div className="flex items-center justify-center space-x-2 text-gray-600">
            <MessageCircle className="w-5 h-5" />
            <p className="text-sm">
              Bạn phải{' '}
              <a href="#" className="text-blue-600 hover:text-blue-800 font-medium">
                đăng nhập
              </a>{' '}
              để gửi phản hồi.
            </p>
          </div>
        </section>
      </article>
    </main>
  );
};

export default BlogPost;
